import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import PopupModal from './components/PopupModal';

function App() {
  const [capsules, setCapsules] = useState([]);
  const [form, setForm] = useState({ recipient_name: '', message: '', open_date: '' });
  const [editingId, setEditingId] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const [popupRecipient, setPopupRecipient] = useState('');


  useEffect(() => {
      axios.get('http://localhost:5000/api/capsules')
        .then(res => {
          setCapsules(res.data);
          const today = new Date().toISOString().split('T')[0];

          console.log("üìÖ Hoje:", today); // mostra a data atual
          console.log("üì¶ C√°psulas:", res.data); // mostra o que veio do backend

          const alreadyShown = JSON.parse(localStorage.getItem('shownCapsules')) || [];

          res.data.forEach(capsule => {
            console.log("‚û°Ô∏è Comparando:", capsule.open_date, "==", today);
            if (capsule.open_date === today && !alreadyShown.includes(capsule.id)) {
              console.log("üéâ Mostrando popup para:", capsule.recipient_name);
              setPopupRecipient(capsule.recipient_name);
              setPopupMessage(capsule.message);
              setShowPopup(true);
              localStorage.setItem('shownCapsules', JSON.stringify([...alreadyShown, capsule.id]));
            }
          });
        });
    }, []);


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editingId) {
      // Atualiza√ß√£o
      await axios.put(`http://localhost:5000/api/capsules/Editar/${editingId}`, form);
      setEditingId(null); // limpa modo edi√ß√£o
    } else {
      // Cria√ß√£o
      await axios.post('http://localhost:5000/api/capsules', form);
    }
    // Limpa formul√°rio e atualiza lista
    setForm({ recipient_name: '', message: '', open_date: '' });
    const res = await axios.get('http://localhost:5000/api/capsules');
    setCapsules(res.data);
  };


  const handleDelete = async (id) => {
    await axios.get(`http://localhost:5000/api/capsules/Deletar/${id}`);
    setCapsules(capsules.filter(c => c.id !== id));
    window.location.reload();
  };

  return (
    <div className="container">
      <h1>C√°psula do Tempo</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome do destinat√°rio"
          value={form.recipient_name}
          required
          onChange={e => setForm({ ...form, recipient_name: e.target.value })}
        />
        <input
          type="date"
          value={form.open_date}
          required
          onChange={e => setForm({ ...form, open_date: e.target.value })}
        />
        <textarea
          placeholder="Mensagem secreta"
          value={form.message}
          required
          onChange={e => setForm({ ...form, message: e.target.value })}
        ></textarea>
        <button className="btn-primary" type="submit">
          {editingId ? 'Salvar Altera√ß√µes' : 'Enviar C√°psula'}
        </button>
        {editingId && (
            <button
              type="button"
              className="btn-cancel"
              onClick={() => {
                setForm({ recipient_name: '', message: '', open_date: '' });
                setEditingId(null);
              }}
            >
              Cancelar Edi√ß√£o
            </button>
          )}
      </form>

      <h2 style={{ marginTop: '2rem' }}>C√°psulas criadas</h2>
      {capsules.map(c => (
        <div key={c.id} className="capsule">
          <p><strong>Para:</strong> {c.recipient_name}</p>
          <p><strong>Abrir em:</strong> {new Date(c.open_date).toLocaleDateString()}</p>
         <button className="btn-primary"  onClick={() => {
            setForm({
              recipient_name: c.recipient_name,
              message: c.message,
              open_date: c.open_date.split('T')[0]
            });
            setEditingId(c.id);
          }}>
            Editar C√°psula
          </button>
          <button   className="btn-danger" onClick={() => handleDelete(c.id)}>
            Deletar C√°psula
          </button>           
        </div>

      ))}
      <PopupModal
        show={showPopup}
        message={popupMessage}
        recipient={popupRecipient}
        onClose={() => setShowPopup(false)}
      />
    </div>

  );
}

export default App;
