import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [capsules, setCapsules] = useState([]);
  const [form, setForm] = useState({ recipient_name: '', message: '', open_date: '' });
  const [editingId, setEditingId] = useState(null);


  useEffect(() => {
    axios.get('http://localhost:5000/api/capsules')
      .then(res => setCapsules(res.data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/capsules', form);
    window.location.reload();
  };

  const handleEdit = async (id) => {
    const response = await axios.get(`http://localhost:5000/api/capsules/Editar/${id}`);
    const capsule = response.data;
    setForm({
      recipient_name: capsule.recipient_name,
      message: capsule.message,
      open_date: capsule.open_date
    });
    await axios.put(`http://localhost:5000/api/capsules/Editar/${id}`, form);
    window.location.reload();
  };

  const handleDelete = async (id) => {
    await axios.get(`http://localhost:5000/api/capsules/Deletar/${id}`);
    setCapsules(capsules.filter(c => c.id !== id));
    window.location.reload();
  };

  return (
    <div className="container">
      <h1>Cápsula do Tempo</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome do destinatário"
          onChange={e => setForm({ ...form, recipient_name: e.target.value })}
        />
        <input
          type="date"
          onChange={e => setForm({ ...form, open_date: e.target.value })}
        />
        <textarea
          placeholder="Mensagem secreta"
          onChange={e => setForm({ ...form, message: e.target.value })}
        ></textarea>
        <button type="submit">Enviar Cápsula</button>
      </form>

      <h2 style={{ marginTop: '2rem' }}>Cápsulas criadas</h2>
      {capsules.map(c => (
        <div key={c.id} className="capsule">
          <p><strong>Para:</strong> {c.recipient_name}</p>
          <p><strong>Abrir em:</strong> {new Date(c.open_date).toLocaleDateString()}</p>
         <button onClick={() => {
            setForm({
              recipient_name: c.recipient_name,
              message: c.message,
              open_date: c.open_date.split('T')[0]
            });
            setEditingId(c.id);
          }}>
            Editar Cápsula
          </button>

          <button onClick={() => handleDelete(c.id)}>
            Deletar Cápsula
          </button>           
        </div>

      ))}
    </div>
  );
}

export default App;
